import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import EmojiPicker from './EmojiPicker';
import './App.css';

const socket = io('http://localhost:4000'); // Backend URL

const App = () => {
  const [username, setUsername] = useState('');
  const [room, setRoom] = useState('');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [isChatting, setIsChatting] = useState(false);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  const joinRoom = () => {
    if (username && room) {
      socket.emit('joinRoom', { username, room });
      setIsChatting(true);
    }
  };

  const sendMessage = () => {
    if (message.trim()) {
      socket.emit('chatMessage', { room, username, message });
      setMessage(''); // Clear message input after sending
    }
  };

  const uploadFile = (file) => {
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        socket.emit('fileMessage', { room, username, file: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      uploadFile(file);
    }
  };

  const handleUploadClick = () => {
    document.getElementById('fileInput').click(); // Programmatically click the hidden file input
  };

  useEffect(() => {
    socket.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    socket.on('fileMessage', ({ username, file }) => {
      setMessages((prev) => [
        ...prev,
        {
          username,
          file,
        },
      ]);
    });

    return () => {
      socket.off('message');
      socket.off('fileMessage');
    };
  }, []);

  const handleEmojiSelect = (emoji) => {
    setMessage((prev) => prev + emoji);
    setShowEmojiPicker(false);
  };

  return (
    <div className="container">
      {!isChatting ? (
        <div className='c'>
          <h2>Join a Chat Room</h2>
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter room number"
            value={room}
            onChange={(e) => setRoom(e.target.value)}
          />
          <button onClick={joinRoom}>Join Room</button>
        </div>
      ) : (
        <div>
          <h2>Chat Room: {room}</h2>
          <div className="chat-container">
            {messages.map((msg, index) => (
              <div key={index} className="message">
                {msg.file ? (
                  <div>
                    <strong>{msg.username}:</strong>
                    <img
                      src={msg.file}
                      alt="Sent file"
                      style={{ maxWidth: '200px', marginTop: '10px' }}
                    />
                  </div>
                ) : (
                  <p>
                    <strong>{msg.username}:</strong> {msg}
                  </p>
                )}
              </div>
            ))}
          </div>
          <input
            type="text"
            placeholder="Enter a message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            style={{ marginRight: '10px', padding: '10px' }}
          />
          <button onClick={sendMessage}>Send</button>
          <button onClick={() => setShowEmojiPicker((prev) => !prev)}>ðŸ˜Š</button>
          <button onClick={handleUploadClick}>ðŸ“¤</button>
          <input
            id="fileInput"
            type="file"
            onChange={handleFileChange}
            style={{ display: 'none' }} // Hidden file input
          />
          {showEmojiPicker && <EmojiPicker onSelect={handleEmojiSelect} />}
        </div>
      )}
    </div>
  );
};

export default App;
