import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000'); // Backend URL

const App = () => {
    const [username, setUsername] = useState('');
    const [room, setRoom] = useState('');
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [isChatting, setIsChatting] = useState(false);

    // Function to handle temporary messages
    const addTemporaryMessage = (msg) => {
        setMessages((prev) => [...prev, msg]);

        // Remove the message after 60 seconds
        setTimeout(() => {
            setMessages((prev) => prev.filter((message) => message !== msg));
        }, 60000); // 60 seconds
    };

    // Join a room
    const joinRoom = () => {
        if (username && room) {
            socket.emit('joinRoom', { username, room });
            setIsChatting(true);
        }
    };

    // Send a message
    const sendMessage = () => {
        if (message.trim()) {
            socket.emit('chatMessage', { room, username, message });
            setMessage('');
        }
    };

    // Listen for incoming messages
    useEffect(() => {
        socket.on('message', (msg) => {
            addTemporaryMessage(msg); // Handle message temporarily
        });

        return () => {
            socket.off('message');
        };
    }, []);

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
            {!isChatting ? (
                <div>
                    <h2>Join a Chat Room</h2>
                    <input
                        type="text"
                        placeholder="Enter your username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        style={{ margin: '10px', padding: '10px' }}
                    />
                    <input
                        type="text"
                        placeholder="Enter room number"
                        value={room}
                        onChange={(e) => setRoom(e.target.value)}
                        style={{ margin: '10px', padding: '10px' }}
                    />
                    <button onClick={joinRoom} style={{ padding: '10px 20px' }}>
                        Join Room
                    </button>
                </div>
            ) : (
                <div>
                    <h2>Chat Room: {room}</h2>
                    <p>Welcome {username} to the room!</p>
                    <div
                        style={{
                            border: '1px solid #ccc',
                            padding: '10px',
                            height: '300px',
                            overflowY: 'scroll',
                            marginBottom: '10px',
                        }}
                    >
                        {messages.map((msg, index) => (
                            <p key={index} style={{ margin: '5px 0' }}>
                                {msg}
                            </p>
                        ))}
                    </div>
                    <input
                        type="text"
                        placeholder="Enter a message"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        style={{ marginRight: '10px', padding: '10px' }}
                    />
                    <button onClick={sendMessage} style={{ padding: '10px 20px' }}>
                        Send
                    </button>
                </div>
            )}
        </div>
    );
};

export default App;
