// EmojiPicker.js
import React from 'react';

const emojis = [
    'ðŸ˜€', 'ðŸ˜‚', 'ðŸ˜', 'ðŸ˜Ž', 'ðŸ˜¢', 'ðŸ˜¡', 'ðŸ‘', 'ðŸ‘Ž', 'ðŸŽ‰', 'â¤ï¸', 'ðŸ”¥', 'âœ¨', 'ðŸŒˆ', 'ðŸŒ', 'ðŸŽˆ'
];

const EmojiPicker = ({ onSelect }) => {
    return (
        <div style={{
            position: 'absolute',
            background: 'white',
            border: '1px solid #ccc',
            borderRadius: '5px',
            padding: '10px',
            zIndex: 1000,
            display: 'flex',
            flexWrap: 'wrap',
            width: '200px'
        }}>
            {emojis.map((emoji, index) => (
                <span
                    key={index}
                    onClick={() => onSelect(emoji)}
                    style={{ fontSize: '24px', cursor: 'pointer', margin: '5px' }}
                >
                    {emoji}
                </span>
            ))}
        </div>
    );
};

export default EmojiPicker;
