import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import EmojiPicker from './EmojiPicker';

const socket = io('http://localhost:4000'); // Backend URL

const App = () => {
    const [username, setUsername] = useState('');
    const [room, setRoom] = useState('');
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [isChatting, setIsChatting] = useState(false);
    const [showEmojiPicker, setShowEmojiPicker] = useState(false); // State for emoji picker visibility

    // Join a room
    const joinRoom = () => {
        if (username && room) {
            socket.emit('joinRoom', { username, room });
            setIsChatting(true);
        }
    };

    // Send a message
    const sendMessage = () => {
        if (message.trim()) {
            socket.emit('chatMessage', { room, username, message });
            setMessage(''); // Clear message input after sending
        }
    };

    // Listen for incoming messages
    useEffect(() => {
        socket.on('message', (msg) => {
            setMessages((prev) => [...prev, msg]);
        });

        return () => {
            socket.off('message'); // Clean up the listener on unmount
        };
    }, []);

    // Handle emoji selection
    const handleEmojiSelect = (emoji) => {
        setMessage((prev) => prev + emoji); // Append the selected emoji to the message
        setShowEmojiPicker(false); // Close the emoji picker after selection
    };

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
            {!isChatting ? (
                <div>
                    <h2>Join a Chat Room</h2>
                    <input
                        type="text"
                        placeholder="Enter your username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        style={{ margin: '10px', padding: '10px' }}
                    />
                    <input
                        type="text"
                        placeholder="Enter room number"
                        value={room}
                        onChange={(e) => setRoom(e.target.value)}
                        style={{ margin: '10px', padding: '10px' }}
                    />
                    <button onClick={joinRoom} style={{ padding: '10px 20px' }}>
                        Join Room
                    </button>
                </div>
            ) : (
                <div>
                    <h2>Chat Room: {room}</h2>
                    <div
                        style={{
                            border: '1px solid #ccc',
                            padding: '10px',
                            height: '300px',
                            overflowY: 'scroll',
                            marginBottom: '10px',
                        }}
                    >
                        {messages.map((msg, index) => (
                            <p key={index} style={{ margin: '5px 0' }}>
                                {msg}
                            </p>
                        ))}
                    </div>
                    <input
                        type="text"
                        placeholder="Enter a message"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        style={{ marginRight: '10px', padding: '10px' }}
                    />
                    <button onClick={sendMessage} style={{ padding: '10px 20px' }}>
                        Send
                    </button>
                    <button 
                        onClick={() => setShowEmojiPicker((prev) => !prev)} // Toggle emoji picker
                        style={{ padding: '10px 20px', marginLeft: '10px' }}
                    >
                        ðŸ˜Š
                    </button>
                    {showEmojiPicker && <EmojiPicker onSelect={handleEmojiSelect} />}
                </div>
            )}
        </div>
    );
};

export default App;
